#!/usr/bin/env python3.8
from Crypto.Util.number import *
from flag import flag
numberN = 2022
numberP = 0x0608
# generate p,q
p = getPrime(numberN)
q = getPrime(numberN-numberP)

n = p*q
d = p
e = pow(d, -1, (p-1)*(q-1))

print("n = ", n)
print("e = ", e)
print("c = ", pow(bytes_to_long(flag), e, n))

